import React,{useState, useEffect} from 'react';
import Chart from "./Chart"

let cri_data = [
    { name: 'CRI', value: 0},
    { name: 'Zero', value: 100},
];

let cct_data = 0;
let illum_data = 0;

function Main(){

    const [data, setCRI] = useState(cri_data);
    const [cct , setCCT] = useState(cct_data);
    const [illum , setILLUM] = useState(illum_data);

    useEffect(()=>{
        let obj_off = document.getElementById("off");
        obj_off.checked = true;
    },[])


    const setCheck =(e) =>{
        if(e.target.id === "on"){
            if(e.target.checked){

            }
        }
        else{
            if(e.target.checked){

            }
        }
    }


    const MouseDown =(obj) =>{
        let range = obj.target.value;
        console.log(range);

        window.addEventListener('mouseup', onMouseUp);
        // if(range<1150){
        //     // obj.target.value = 1000;
        // }

    }

    const onMouseUp =(e) =>{

        if(e.target.id === "range_cct" || e.target.id ==="range_illum"){
            console.log("up: "+ e.target.value)
            let value = e.target.value;
    
            if(e.target.id ==="range_illum"){
                if(value<1050){
                    e.target.value = 1000;
                }
                else if(value>=1050 && value<1150){
                    e.target.value = 1100;
                }
                else if(value<=1200){
                    e.target.value = 1200;
                }
            }
            else if(e.target.id ==="range_cct"){
                if(value<3500){
                    e.target.value = 3000;
                }
                else if(value>=3500 && value<4500){
                    e.target.value = 4000;
                }
                else if(value<=5000){
                    e.target.value = 5000;
                }
            }
        }

    }

    return(
        <div className='background'>
            <div className='dashboard'>
                <div className='title'>DashBoard</div>
                <div className='dashboard-content'>
                    <div className='cri'>
                        <p>CRI</p>
                        <h4>연색지수</h4>
                        <hr></hr>
                        <div class="box">
                            <Chart data ={data}/>
                        </div>
                    </div>
                    <section>
                        <div className='cct'>
                            <p>CCT</p>
                            <h4>색온도</h4>
                            <hr></hr>
                            <h3>{cct}<span>K</span></h3>
                        </div>
                        <div className='illum'>
                            <p>illumination</p>
                            <h4>조도</h4>
                            <hr></hr>
                            <h3>{illum}<span>LUX</span></h3>
                        </div>
                    </section>
                </div>
            </div>
            <div className='control'>
                <div className='title'>Control</div>
                <div className='control-box'>
                    <section className='light'>
                        <h4>조명</h4>

                        <div className="light-ctl">
                            <div className="radio-checked">
                                <div className="radio-checked_highlight"></div>
                                    <div className="radio-checked_container">
                                    <input className="radio-checked_input" id="on" name="status" type="radio" value="on" onClick ={(e) =>{setCheck(e)}}/>
                                    <label className="radio-checked_label radio-checked_label--on" for="on"> On</label>
                                    <input className="radio-checked_input" id="off" name="status" type="radio" value="off" onClick ={(e) =>{setCheck(e)}} />
                                    <label className="radio-checked_label radio-checked_label--off" for="off">Off</label>
                                    </div>
                            </div>
                        </div>
                    </section>

                    <section className='cct-ctl'>
                        <h4>색온도(K)</h4>
                        
                        <div className="range">
                            <input id="range_cct" type="range"  min="3000" max="5000" step="100" onMouseDown={(e)=>MouseDown(e)}></input>
                            <div className='range__ticks'>
                                <span className="range__tick"><span className ="range__tick-text">3000</span></span>
                                <span className="range__tick"><span className ="range__tick-text">4000</span></span>
                                <span className="range__tick"><span className ="range__tick-text">5000</span></span>
                            </div>
                        </div>
                    </section>
                    <section className='illum-ctl'>
                        <h4>조도(LUX)</h4>
                        
                        <div className="range">
                            <input id="range_illum" type="range" min="1000" max="1200" step="10"  onMouseDown={(e)=>MouseDown(e)}></input>
                            <div className='range__ticks'>
                                <span className="range__tick"><span className ="range__tick-text">1000</span></span>
                                <span className="range__tick"><span className ="range__tick-text">1100</span></span>
                                <span className="range__tick"><span className ="range__tick-text">1200</span></span>
                            </div>
                        </div>
                    </section>
                </div>
                    
            </div>
        </div>
    )

}

export default Main;
